import React, { useState, useEffect } from "react";
import axios from "axios";

import { useNavigate, useParams } from "react-router-dom";

export default function UpdateStudent() {

    const [name, setName] = useState("");
    const [age, setAge] = useState("");
    const [gender, setGender] = useState("");
    const navigate = useNavigate();
    const { id } = useParams();

    // console.log(id);

    useEffect(() => {
        getUserById();
    }, []);

    const getUserById = async () => {
        const response = await axios.get(`http://localhost:8070/student/get/${id}`);
        setName(response.data.student.name);
        setAge(response.data.student.age);
        setGender(response.data.student.gender);

        console.log(response);
        console.log(response.data.student.name);

    };

    return (

        <div className="container">
            <form  >
                <div className="form-group">
                    <label for="name">Student Name</label>
                    <input type="text" className="form-control" id="name"  placeholder="Enter Student Name"
                        onChange={(e) => {
                            setName(e.target.value);
                        }} />
                </div>
                <div className="form-group">
                    <label for="age">Student Age</label>
                    <input type="text" className="form-control" id="age" placeholder="Enter Student Age"
                        onChange={(e) => {
                            setAge(e.target.value);
                        }} />
                </div>
                <div className="form-group">
                    <label for="gender">Student Gender</label>
                    <input type="text" className="form-control" id="gender" placeholder="Enter Gender"
                        onChange={(e) => {
                            setGender(e.target.value);
                        }} />
                </div>

                <button type="submit" className="btn btn-primary">Submit</button>
            </form>
        </div>
    )
}
